import React, { useState } from 'react';
import { createCalendarInvite } from '../../api';
import './CalendarWidget.css';

const CalendarWidget = ({ expanded = false }) => {
  const [inviteData, setInviteData] = useState({
    details: ''
  });
  const [generatedInvite, setGeneratedInvite] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const generateInvite = async () => {
    if (!inviteData.details.trim()) return;

    setLoading(true);
    setError(null);

    try {
      const response = await createCalendarInvite(inviteData);
      setGeneratedInvite(response.structured_details || response.invite_status || 'Failed to generate invite');
    } catch (err) {
      console.error('Failed to generate invite:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const clearInvite = () => {
    setInviteData({ details: '' });
    setGeneratedInvite('');
    setError(null);
  };

  const upcomingMeetings = [
    { title: 'Client Review - Alice Smith', time: '2:00 PM', date: 'Today', type: 'review' },
    { title: 'Portfolio Planning - Bob Jones', time: '10:00 AM', date: 'Tomorrow', type: 'planning' },
    { title: 'Risk Assessment Meeting', time: '3:30 PM', date: 'Aug 9', type: 'assessment' },
    { title: 'Quarterly Team Sync', time: '9:00 AM', date: 'Aug 10', type: 'team' }
  ];

  const getMeetingIcon = (type) => {
    switch (type) {
      case 'review':
        return (
          <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <path d="M9 11l3 3 8-8"></path>
            <path d="M21 12c0 4.97-4.03 9-9 9s-9-4.03-9-9 4.03-9 9-9c1.97 0 3.78.64 5.25 1.72"></path>
          </svg>
        );
      case 'planning':
        return (
          <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <path d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386L8.46 15.54z"></path>
          </svg>
        );
      case 'assessment':
        return (
          <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <path d="M3 3v18h18"></path>
            <path d="M18.7 8l-5.1 5.2-2.8-2.7L7 14.3"></path>
          </svg>
        );
      default:
        return (
          <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"></path>
            <circle cx="12" cy="7" r="4"></circle>
          </svg>
        );
    }
  };

  return (
    <div className={`calendar-widget ${expanded ? 'expanded' : ''}`}>
      <div className="widget-header">
        <div className="header-content">
          <h3>Calendar & Meetings</h3>
          <span className="meeting-count">{upcomingMeetings.length} upcoming</span>
        </div>
      </div>

      <div className="widget-content">
        {expanded && (
          <div className="create-invite-section">
            <h4>Create Meeting Invite</h4>
            <div className="invite-form">
              <div className="form-group">
                <label htmlFor="meeting-details">Meeting Details</label>
                <textarea
                  id="meeting-details"
                  value={inviteData.details}
                  onChange={(e) => setInviteData(prev => ({ ...prev, details: e.target.value }))}
                  placeholder="Describe the meeting (e.g., 'Portfolio review meeting with Alice Smith next Tuesday at 2 PM')"
                  rows={3}
                  className="details-input"
                />
              </div>

              <div className="form-actions">
                <button 
                  onClick={generateInvite}
                  disabled={!inviteData.details.trim() || loading}
                  className="generate-invite-btn"
                >
                  {loading ? (
                    <>
                      <div className="spinner-small"></div>
                      Processing...
                    </>
                  ) : (
                    <>
                      <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                        <rect x="3" y="4" width="18" height="18" rx="2" ry="2"></rect>
                        <line x1="16" y1="2" x2="16" y2="6"></line>
                        <line x1="8" y1="2" x2="8" y2="6"></line>
                        <line x1="3" y1="10" x2="21" y2="10"></line>
                      </svg>
                      Create Invite
                    </>
                  )}
                </button>
                <button 
                  onClick={clearInvite}
                  className="clear-btn"
                >
                  Clear
                </button>
              </div>
            </div>

            {error && (
              <div className="error-message">
                <p>Failed to create invite: {error}</p>
              </div>
            )}

            {generatedInvite && (
              <div className="generated-invite">
                <div className="invite-header">
                  <h4>Meeting Invite</h4>
                </div>
                <div className="invite-content">
                  <pre>{generatedInvite}</pre>
                </div>
                <div className="invite-actions">
                  <button className="send-invite-btn">
                    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                      <line x1="22" y1="2" x2="11" y2="13"></line>
                      <polygon points="22,2 15,22 11,13 2,9 22,2"></polygon>
                    </svg>
                    Send Invite
                  </button>
                </div>
              </div>
            )}
          </div>
        )}

        <div className="upcoming-meetings">
          <h4>Upcoming Meetings</h4>
          <div className="meetings-list">
            {upcomingMeetings.slice(0, expanded ? upcomingMeetings.length : 3).map((meeting, index) => (
              <div key={index} className="meeting-item">
                <div className="meeting-icon">
                  {getMeetingIcon(meeting.type)}
                </div>
                <div className="meeting-info">
                  <div className="meeting-title">{meeting.title}</div>
                  <div className="meeting-time">
                    <span className="meeting-date">{meeting.date}</span>
                    <span className="meeting-time-slot">{meeting.time}</span>
                  </div>
                </div>
                <div className="meeting-actions">
                  <button className="meeting-action-btn" title="Join Meeting">
                    <svg width="14" height="14" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                      <polygon points="23 7 16 12 23 17 23 7"></polygon>
                      <rect x="1" y="5" width="15" height="14" rx="2" ry="2"></rect>
                    </svg>
                  </button>
                  <button className="meeting-action-btn" title="Edit Meeting">
                    <svg width="14" height="14" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                      <path d="M11 4H4a2 2 0 0 0-2 2v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2-2v-7"></path>
                      <path d="M18.5 2.5a2.121 2.121 0 0 1 3 3L12 15l-4 1 1-4 9.5-9.5z"></path>
                    </svg>
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>

        {!expanded && upcomingMeetings.length > 3 && (
          <div className="widget-footer">
            <button className="view-all-btn">
              View all meetings
            </button>
          </div>
        )}

        {expanded && (
          <div className="calendar-quick-actions">
            <h4>Quick Actions</h4>
            <div className="quick-action-buttons">
              <button 
                onClick={() => setInviteData(prev => ({ ...prev, details: 'Weekly client portfolio review meeting' }))}
                className="quick-action-btn"
              >
                Portfolio Review
              </button>
              <button 
                onClick={() => setInviteData(prev => ({ ...prev, details: 'Quarterly business review and planning session' }))}
                className="quick-action-btn"
              >
                Business Review
              </button>
              <button 
                onClick={() => setInviteData(prev => ({ ...prev, details: 'Risk assessment and compliance meeting' }))}
                className="quick-action-btn"
              >
                Risk Assessment
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default CalendarWidget;
