import React, { useState, useEffect } from 'react';
import { chat } from '../../api';
import './ClientWidget.css';

const ClientWidget = ({ expanded = false }) => {
  const [clientData, setClientData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedClient, setSelectedClient] = useState(null);

  useEffect(() => {
    loadClientData();
  }, []);

  const loadClientData = async () => {
    try {
      setLoading(true);
      setError(null);

      // Get client-related data using the chat endpoint
      const topClientsResponse = await chat({ 
        message: "show me top clients by portfolio value" 
      });

      const clientDistributionResponse = await chat({ 
        message: "show me client distribution by advisor" 
      });

      // Parse the responses to extract client information
      setClientData({
        topClients: parseClientData(topClientsResponse.response),
        distribution: parseDistributionData(clientDistributionResponse.response)
      });
    } catch (err) {
      console.error('Failed to fetch client data:', err);
      setError(err.message);
      // Fallback data
      setClientData({
        topClients: [
          { name: "Alice Smith", value: 800000, advisor: "John Smith", riskLevel: "Conservative" },
          { name: "Carol White", value: 900000, advisor: "John Smith", riskLevel: "Moderate" },
          { name: "Bob Jones", value: 600000, advisor: "Sarah Johnson", riskLevel: "Aggressive" },
          { name: "David Wilson", value: 300000, advisor: "Michael Brown", riskLevel: "Moderate" },
          { name: "Emma Davis", value: 450000, advisor: "Sarah Johnson", riskLevel: "Conservative" }
        ],
        distribution: [
          { advisor: "John Smith", clients: 2 },
          { advisor: "Sarah Johnson", clients: 2 },
          { advisor: "Michael Brown", clients: 1 }
        ]
      });
    } finally {
      setLoading(false);
    }
  };

  const parseClientData = (response) => {
    // This would parse the AI response to extract client data
    // For now, return mock data that matches the expected format
    return [
      { name: "Alice Smith", value: 800000, advisor: "John Smith", riskLevel: "Conservative", lastContact: "2024-08-05" },
      { name: "Carol White", value: 900000, advisor: "John Smith", riskLevel: "Moderate", lastContact: "2024-08-04" },
      { name: "Bob Jones", value: 600000, advisor: "Sarah Johnson", riskLevel: "Aggressive", lastContact: "2024-08-06" },
      { name: "David Wilson", value: 300000, advisor: "Michael Brown", riskLevel: "Moderate", lastContact: "2024-08-03" },
      { name: "Emma Davis", value: 450000, advisor: "Sarah Johnson", riskLevel: "Conservative", lastContact: "2024-08-07" }
    ];
  };

  const parseDistributionData = (response) => {
    return [
      { advisor: "John Smith", clients: 2 },
      { advisor: "Sarah Johnson", clients: 2 },
      { advisor: "Michael Brown", clients: 1 }
    ];
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const getRiskColor = (riskLevel) => {
    switch (riskLevel?.toLowerCase()) {
      case 'conservative': return '#10b981'; // green
      case 'moderate': return '#f59e0b'; // yellow
      case 'aggressive': return '#ef4444'; // red
      default: return '#6b7280'; // gray
    }
  };

  const getContactStatus = (lastContact) => {
    if (!lastContact) return 'unknown';
    const contactDate = new Date(lastContact);
    const today = new Date();
    const diffDays = Math.floor((today - contactDate) / (1000 * 60 * 60 * 24));

    if (diffDays <= 1) return 'recent';
    if (diffDays <= 7) return 'week';
    return 'overdue';
  };

  if (loading) {
    return (
      <div className={`client-widget ${expanded ? 'expanded' : ''}`}>
        <div className="widget-header">
          <h3>Client Overview</h3>
          <div className="loading-spinner">
            <div className="spinner-small"></div>
          </div>
        </div>
        <div className="widget-content">
          <div className="loading-state">Loading client data...</div>
        </div>
      </div>
    );
  }

  if (error && !clientData) {
    return (
      <div className={`client-widget ${expanded ? 'expanded' : ''}`}>
        <div className="widget-header">
          <h3>Client Overview</h3>
          <button onClick={loadClientData} className="refresh-btn" title="Retry">
            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <polyline points="23 4 23 10 17 10"></polyline>
              <polyline points="1 20 1 14 7 14"></polyline>
              <path d="M20.49 9A9 9 0 0 0 5.64 5.64L1 10m22 4l-4.64 4.36A9 9 0 0 1 3.51 15"></path>
            </svg>
          </button>
        </div>
        <div className="widget-content">
          <div className="error-state">
            <p>Failed to load client data</p>
            <small>{error}</small>
          </div>
        </div>
      </div>
    );
  }

  const topClients = clientData?.topClients || [];
  const displayClients = expanded ? topClients : topClients.slice(0, 4);

  return (
    <div className={`client-widget ${expanded ? 'expanded' : ''}`}>
      <div className="widget-header">
        <div className="header-content">
          <h3>Client Overview</h3>
          <span className="client-count">{topClients.length} clients</span>
        </div>
        <button onClick={loadClientData} className="refresh-btn" title="Refresh">
          <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <polyline points="23 4 23 10 17 10"></polyline>
            <polyline points="1 20 1 14 7 14"></polyline>
            <path d="M20.49 9A9 9 0 0 0 5.64 5.64L1 10m22 4l-4.64 4.36A9 9 0 0 1 3.51 15"></path>
          </svg>
        </button>
      </div>

      <div className="widget-content">
        {topClients.length === 0 ? (
          <div className="empty-state">
            <svg width="48" height="48" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"></path>
              <circle cx="12" cy="7" r="4"></circle>
            </svg>
            <p>No client data available</p>
          </div>
        ) : (
          <div className="client-list">
            {displayClients.map((client, index) => {
              const contactStatus = getContactStatus(client.lastContact);

              return (
                <div
                  key={client.name}
                  className={`client-item ${selectedClient === index ? 'selected' : ''}`}
                  onClick={() => setSelectedClient(selectedClient === index ? null : index)}
                >
                  <div className="client-avatar">
                    <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                      <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"></path>
                      <circle cx="12" cy="7" r="4"></circle>
                    </svg>
                  </div>

                  <div className="client-info">
                    <div className="client-header">
                      <span className="client-name">{client.name}</span>
                      <div className="client-badges">
                        <span 
                          className="risk-badge" 
                          style={{ backgroundColor: getRiskColor(client.riskLevel) }}
                        >
                          {client.riskLevel}
                        </span>
                        <span className={`contact-status ${contactStatus}`}>
                          {contactStatus === 'recent' && '🟢'}
                          {contactStatus === 'week' && '🟡'}
                          {contactStatus === 'overdue' && '🔴'}
                        </span>
                      </div>
                    </div>

                    <div className="client-details">
                      <div className="client-value">{formatCurrency(client.value)}</div>
                      <div className="client-advisor">Advisor: {client.advisor}</div>
                    </div>

                    {expanded && (
                      <div className="client-meta">
                        <span className="last-contact">
                          Last contact: {client.lastContact || 'Not recorded'}
                        </span>
                      </div>
                    )}
                  </div>

                  <div className="client-actions">
                    <button className="action-btn" title="Call Client">
                      <svg width="14" height="14" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                        <path d="M22 16.92v3a2 2 0 0 1-2.18 2 19.79 19.79 0 0 1-8.63-3.07 19.5 19.5 0 0 1-6-6 19.79 19.79 0 0 1-3.07-8.67A2 2 0 0 1 4.11 2h3a2 2 0 0 1 2 1.72 12.84 12.84 0 0 0 .7 2.81 2 2 0 0 1-.45 2.11L8.09 9.91a16 16 0 0 0 6 6l1.27-1.27a2 2 0 0 1 2.11-.45 12.84 12.84 0 0 0 2.81.7A2 2 0 0 1 22 16.92z"></path>
                      </svg>
                    </button>
                    <button className="action-btn" title="Send Message">
                      <svg width="14" height="14" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                        <path d="M4 4h16c1.1 0 2 .9 2 2v12c0 1.1-.9 2-2 2H4c-1.1 0-2-.9-2-2V6c0-1.1.9-2 2-2z"></path>
                        <polyline points="22,6 12,13 2,6"></polyline>
                      </svg>
                    </button>
                  </div>
                </div>
              );
            })}
          </div>
        )}

        {!expanded && topClients.length > 4 && (
          <div className="widget-footer">
            <button className="view-all-btn">
              View all {topClients.length} clients
            </button>
          </div>
        )}

        {expanded && clientData?.distribution && (
          <div className="distribution-section">
            <h4>Client Distribution</h4>
            <div className="distribution-chart">
              {clientData.distribution.map((item, index) => (
                <div key={item.advisor} className="distribution-item">
                  <div className="advisor-name">{item.advisor}</div>
                  <div className="distribution-bar">
                    <div 
                      className="bar-fill"
                      style={{ 
                        width: `${(item.clients / Math.max(...clientData.distribution.map(d => d.clients))) * 100}%`,
                        backgroundColor: `hsl(${index * 120}, 70%, 50%)`
                      }}
                    ></div>
                  </div>
                  <div className="client-count">{item.clients} clients</div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ClientWidget;
