// api.js - helper for backend API calls with authentication

const API_BASE = process.env.REACT_APP_API_URL || 'https://apialchemistproject-backend-608187465720.us-central1.run.app';

// Helper function to get auth headers
const getAuthHeaders = () => {
  const token = localStorage.getItem('gcp_identity_token');
  console.log('Token from localStorage:', token); // Debug log
  console.log('All localStorage items:', Object.keys(localStorage)); // Debug log

  return token ? {
    'Authorization': `Bearer ${token}`,
    'Content-Type': 'application/json'
  } : {
    'Content-Type': 'application/json'
  };
};

// Helper function to handle API responses with better error messages
const handleResponse = async (response) => {
  if (!response.ok) {
    let errorMessage = `HTTP ${response.status}: ${response.statusText}`;

    try {
      const errorData = await response.json();
      if (errorData.detail) {
        errorMessage += ` - ${errorData.detail}`;
      }
    } catch (e) {
      // Response might not be JSON
    }

    if (response.status === 401 || response.status === 403) {
      errorMessage += ' - Please check your authentication token';
      // Clear invalid token
      localStorage.removeItem('gcp_identity_token');
      localStorage.removeItem('user_email');
    }

    throw new Error(errorMessage);
  }
  return response.json();
};

export async function fetchTodo() {
  const res = await fetch(`${API_BASE}/todo`, {
    headers: getAuthHeaders()
  });
  return handleResponse(res);
}

export async function fetchNBA() {
  const res = await fetch(`${API_BASE}/nba`, {
    headers: getAuthHeaders()
  });
  return handleResponse(res);
}

export async function draftMessage(data) {
  const res = await fetch(`${API_BASE}/draft-message`, {
    method: 'POST',
    headers: getAuthHeaders(),
    body: JSON.stringify(data)
  });
  return handleResponse(res);
}

export async function createCalendarInvite(data) {
  const res = await fetch(`${API_BASE}/calendar-invite`, {
    method: 'POST',
    headers: getAuthHeaders(),
    body: JSON.stringify(data)
  });
  return handleResponse(res);
}

export async function summarize(data) {
  const res = await fetch(`${API_BASE}/summarize`, {
    method: 'POST',
    headers: getAuthHeaders(),
    body: JSON.stringify(data)
  });
  return handleResponse(res);
}

export async function ingestData(data) {
  const res = await fetch(`${API_BASE}/ingest-data`, {
    method: 'POST',
    headers: getAuthHeaders(),
    body: JSON.stringify(data)
  });
  return handleResponse(res);
}

export async function fetchAggregation() {
  const res = await fetch(`${API_BASE}/aggregation`, {
    headers: getAuthHeaders()
  });
  return handleResponse(res);
}

export async function chat(data) {
  console.log('Chat API called with:', data);
  console.log('API Base URL:', API_BASE);

  const headers = getAuthHeaders();
  console.log('Auth Headers:', headers);

  try {
    const res = await fetch(`${API_BASE}/chat`, {
      method: 'POST',
      headers: headers,
      body: JSON.stringify(data)
    });

    console.log('Response status:', res.status);
    console.log('Response headers:', res.headers);

    if (!res.ok) {
      const errorText = await res.text();
      console.error('Response error:', errorText);
      throw new Error(`HTTP ${res.status}: ${errorText}`);
    }

    const result = await res.json();
    console.log('Response data:', result);
    return result;
  } catch (error) {
    console.error('Fetch error:', error);
    throw error;
  }
}
