import React, { useState } from 'react';
import { useAuth } from '../contexts/TokenAuthContext';
import './SimpleLogin.css';

const SimpleLogin = ({ children }) => {
  const { user, signIn, loading, authError } = useAuth();
  const [isSigningIn, setIsSigningIn] = useState(false);

  // If user is authenticated, show the main app
  if (user && user.authenticated) {
    return <>{children}</>;
  }

  const handleSignIn = async () => {
    setIsSigningIn(true);
    try {
      await signIn();
    } catch (error) {
      console.error('Sign in error:', error);
    } finally {
      setIsSigningIn(false);
    }
  };

  // Show loading state during authentication initialization
  if (loading) {
    return (
      <div className="simple-login-container">
        <div className="simple-login-card">
          <h1>Private Banking Advisor Copilot</h1>
          <div className="loading-spinner">
            <div className="spinner"></div>
            <p>Initializing...</p>
          </div>
        </div>
      </div>
    );
  }

  // Show sign-in page
  return (
    <div className="simple-login-container">
      <div className="simple-login-card">
        <h1>Private Banking Advisor Copilot</h1>
        <p>Welcome to your banking dashboard</p>

        {authError && (
          <div className="error-message">
            <p>⚠️ {authError}</p>
          </div>
        )}

        <button 
          onClick={handleSignIn} 
          disabled={isSigningIn}
          className="sign-in-button"
        >
          {isSigningIn ? (
            <>
              <div className="button-spinner"></div>
              Signing in...
            </>
          ) : (
            <>
              <svg width="18" height="18" viewBox="0 0 24 24">
                <path fill="#4285F4" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"/>
                <path fill="#34A853" d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"/>
                <path fill="#FBBC05" d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"/>
                <path fill="#EA4335" d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"/>
              </svg>
              Sign in with Google
            </>
          )}
        </button>

        <div className="info-text">
          <p><small>Demo authentication - click to access dashboard</small></p>
        </div>
      </div>
    </div>
  );
};

export default SimpleLogin;
